mapper.select:

SELECT SEAT_ID FROM SEAT 
WHERE FLIGHT_ID = #{flightId} 
GROUP BY SEAT_END_NO

	SELECT DISTINCT A.SEAT_TOTAL - C.R_CNT AS REMAIN_SEAT
   FROM SEAT A
      , (
         SELECT COUNT(*) AS R_CNT
             , B.FLIGHT_ID
                 , B.SEAT_ID
            FROM RESERVATION B
                WHERE B.FLIGHT_ID = #{flightId}
                  AND B.SEAT_ID = #{seatId}
       ) C
   WHERE A.FLIGHT_ID = C.FLIGHT_ID
      AND A.SEAT_ID = C.SEAT_ID;
